class PomodoroTimer {
    constructor() {
        this.workTime = 20 * 60; // 20 minutes in seconds
        this.breakTime = 5 * 60; // 5 minutes in seconds
        this.timeLeft = this.workTime;
        this.isRunning = false;
        this.isWorkMode = true;
        this.timer = null;

        // DOM elements
        this.minutesDisplay = document.getElementById('minutes');
        this.secondsDisplay = document.getElementById('seconds');
        this.startButton = document.getElementById('start');
        this.pauseButton = document.getElementById('pause');
        this.resetButton = document.getElementById('reset');
        this.workButton = document.getElementById('work');
        this.breakButton = document.getElementById('break');
        this.bootybuttButton = document.getElementById('bootybutt');
        this.lamoscaButton = document.getElementById('lamosca');

        // Event listeners
        this.startButton.addEventListener('click', () => this.start());
        this.pauseButton.addEventListener('click', () => this.pause());
        this.resetButton.addEventListener('click', () => this.reset());
        this.workButton.addEventListener('click', () => this.setWorkMode());
        this.breakButton.addEventListener('click', () => this.setBreakMode());
        this.bootybuttButton.addEventListener('click', () => this.setTheme('bootybutt'));
        this.lamoscaButton.addEventListener('click', () => this.setTheme('lamosca'));

        // Initialize display and theme
        this.updateDisplay();
        this.setTheme('bootybutt'); // Start with Booty-Butt theme
    }

    updateDisplay() {
        const minutes = Math.floor(this.timeLeft / 60);
        const seconds = this.timeLeft % 60;
        this.minutesDisplay.textContent = minutes.toString().padStart(2, '0');
        this.secondsDisplay.textContent = seconds.toString().padStart(2, '0');
    }

    start() {
        // First, check if the timer is already running
        if (!this.isRunning) {
            // If it's not running, let's start it!
            this.isRunning = true;
            
            // This is like setting up a repeating alarm that goes off every second (1000 milliseconds)
            this.timer = setInterval(() => {
                // Every second, we:
                this.timeLeft--;                // 1. Count down by one second
                this.updateDisplay();           // 2. Update the numbers on the screen
                
                // 3. Check if we've reached zero
                if (this.timeLeft === 0) {
                    this.playAlarm();           // Play a "beep" sound
                    
                    // 4. Switch between work and break modes
                    if (this.isWorkMode) {
                        this.setBreakMode();    // If we were working, time for a break!
                    } else {
                        this.setWorkMode();     // If we were on break, back to work!
                    }
                }
            }, 1000);  // Do this every 1000 milliseconds (1 second)
        }
    }

    pause() {
        if (this.isRunning) {
            clearInterval(this.timer);
            this.isRunning = false;
        }
    }

    reset() {
        this.pause();
        this.timeLeft = this.isWorkMode ? this.workTime : this.breakTime;
        this.updateDisplay();
    }

    setWorkMode() {
        this.pause();
        this.isWorkMode = true;
        this.timeLeft = this.workTime;
        this.updateDisplay();
        this.workButton.classList.add('active');
        this.breakButton.classList.remove('active');
    }

    setBreakMode() {
        this.pause();
        this.isWorkMode = false;
        this.timeLeft = this.breakTime;
        this.updateDisplay();
        this.workButton.classList.remove('active');
        this.breakButton.classList.add('active');
    }

    playAlarm() {
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
    }

    setTheme(theme) {
        // Remove all theme classes
        document.body.classList.remove('bootybutt-theme', 'lamosca-theme');
        
        // Add the selected theme class
        document.body.classList.add(`${theme}-theme`);
        
        // Update active states of theme buttons
        this.bootybuttButton.classList.toggle('active', theme === 'bootybutt');
        this.lamoscaButton.classList.toggle('active', theme === 'lamosca');
    }
}

// Initialize the timer when the page loads
document.addEventListener('DOMContentLoaded', () => {
    new PomodoroTimer();
}); 